class Logger{constructor(context='APP'){this.context=context;this.debugEnabled=this._shouldEnableDebug();}
_shouldEnableDebug(){const urlParams=new URLSearchParams(window.location.search);if(urlParams.get('debug')==='true'){return true;}
const scopedKey=(window.BT_STORAGE&&typeof window.BT_STORAGE.key==='function')?window.BT_STORAGE.key('batchtrack_debug'):'batchtrack_debug';if(localStorage.getItem(scopedKey)==='true'){return true;}
return false;}
debug(message,...args){if(this.debugEnabled){console.log(`ðŸ” ${this.context}: ${message}`,...args);}}
info(...args){console.info('â„¹ï¸',...args);}
warn(...args){console.warn('âš ï¸',...args);}
error(...args){console.error('âŒ',...args);}
perf(message,startTime){const duration=performance.now()-startTime;console.log(`â±ï¸ ${message}: ${duration.toFixed(2)}ms`);}}
const logger=new Logger('APP');export{logger};export{Logger};export const debug=(...args)=>logger.debug(...args);export const info=(...args)=>logger.info(...args);export const warn=(...args)=>logger.warn(...args);export const error=(...args)=>logger.error(...args);export const perf=(message,startTime)=>logger.perf(message,startTime);export default logger;if(logger.debugEnabled&&window.location.hostname.includes('replit.dev')){console.log('ðŸ”§ DEBUG MODE: Frontend debugging enabled');}
